public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Evaluation of expression: the operands are primitive explicit int types, the outcome is int also = " + (2+3));
		System.out.println("Evaluation of expression: the operands are primitive explicit float types, the outcome is float also = " + (2.0 + 3.0));
		System.out.println("Evaluation of expression: the operands are primitive explicit int types, the outcome is forced to cast into float = " +(float) (2+3));
		
	
	}

	
}

